import React from 'react';
import { Link } from 'react-router-dom';

function ServiceCategory({ link, category, currentService }) {
    const isActive = currentService === category;

    const liStyle = {
        backgroundColor: isActive ? 'blue !important' : 'transparent !important',
    };

    return (
        <li style={liStyle}>
            <Link to={link}>{category}</Link>
        </li>
    );
}

export default ServiceCategory;
