function Hero1() {
    const client = new Client()
    .setEndpoint("https://sai.sakicelik.com.tr/v1") // Appwrite Endpoint
    .setProject("654d24a6efcad9eb4ff2");

    const databases = new Databases(client);

    let promise = databases.listDocuments(
        "654d42331f1604783143",
        "65646b7549f9132d58c5",
    );

    promise.then(function (response) {
        console.log(response);
    }, function (error) {
        console.log(error);
    });

    const [documents, setDocuments] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
          try {
            client
            .setEndpoint("https://sai.sakicelik.com.tr/v1") // Appwrite Endpoint
            .setProject('654d24a6efcad9eb4ff2');

            const databases = new Databases(client);

            const response = await databases.listDocuments(
                "654d42331f1604783143",
                "65646b7549f9132d58c5",
            );

            setDocuments(response.documents);
          } catch (error) {
            console.error('Error fetching documents:', error);
          }
        };

        fetchData();
        const sortedHeroData = documents.slice().sort((a, b) => b.id - a.id);

      }, []);

    const params = {
        spaceBetween: 30,
        centeredSlides: true,
        autoplay: {
          delay: 250,
          disableOnInteraction: false
        },
        pagination: {
          el: '.swiper-pagination',
          clickable: true
        },
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev'
        }
      }

    return (
        <section className="hero-slide-wrapper hero-1">
                   <Swiper {...params}
  className="hero-slider-active owl-carousel">

                {sortedHeroData.map((slide, index) => (
                    <div key={index}>
                        <div className="single-slide bg-cover" style={{ backgroundImage: `url(${slide.backgroundImage})` }}>
                            <div className="container">
                                <div className="row">
                                    <div className="col-12 col-lg-8 col-xl-6">
                                        <div className="hero-contents">
                                            <h1>{slide.title}</h1>
                                            <Link to={slide.servicesLink} style={{ color: 'white', fontWeight: 'semibold' }} className="theme-btn font-weight-bold">
                                                Hizmetlerimiz <BsArrowRight style={{ marginLeft: '10px' }} />
                                            </Link>
                                            <Link to={slide.exploreLink} style={{backgroundColor: '#0071DE', border: '2px solid #0071DE', fontWeight: 'medium', color: 'white' }} className="theme-btn">
                                                {slide.exploreBtnText}
                                            </Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </Swiper>
        </section>
    );
}

export default Hero1;
