import React, { useEffect, useState } from 'react';
import { BsArrowRight } from 'react-icons/bs';
import { Link } from 'react-router-dom';
import SwiperCore, { Autoplay } from 'swiper';
import { Client, Databases } from "appwrite";
import 'swiper/swiper-bundle.min.css';
import {Swiper,SwiperSlide} from 'swiper/react';

SwiperCore.use([Autoplay]);

function Hero1() {

  const client = new Client()
    .setEndpoint("https://sai.sakicelik.com.tr/v1") // Appwrite Endpoint
    .setProject("654d24a6efcad9eb4ff2");

  const databases = new Databases(client);

  const [documents, setDocuments] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await databases.listDocuments(
          "654d42331f1604783143",
          "65646b7549f9132d58c5",
        );

        setDocuments(response.documents);
      } catch (error) {
        console.error('Error fetching documents:', error);
      }
    };

    fetchData();

  }, [databases]);

  const sortedHeroData = documents.slice().sort((a, b) => b.id - a.id);

  const params = {
    spaceBetween: 30,
    centeredSlides: true,
    autoplay: {
      delay: 2500,
      disableOnInteraction: false
    },
    pagination: {
      el: '.swiper-pagination',
      clickable: true
    },
    navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev'
    }
  }

  useEffect(() => {
    const swiper = new Swiper('.hero-slider-active', params);
    swiper.update();
  }, [sortedHeroData]);

  return (
    <section className="hero-slide-wrapper hero-1">
      <Swiper {...params} className="hero-slider-active owl-carousel">
        {sortedHeroData.map((slide, index) => (
          <SwiperSlide key={index}>
            <div className="single-slide bg-cover" style={{ backgroundImage: `url(${slide.backgroundImage})` }}>
              <div className="container">
                <div className="row">
                  <div className="col-12 col-lg-8 col-xl-6">
                    <div className="hero-contents">
                      <h1>{slide.title}</h1>
                      <Link to={slide.servicesLink} className="theme-btn font-weight-bold">
                        Hizmetlerimiz <BsArrowRight style={{ marginLeft: '10px' }} />
                      </Link>
                      <Link to={slide.exploreLink} className="theme-btn" style={{ backgroundColor: '#0071DE', border: '2px solid #0071DE', fontWeight: 'medium', color: 'white' }}>
                        {slide.exploreBtnText}
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
}

export default Hero1;
