import React from 'react';
import { BsArrowRight } from 'react-icons/bs';
import { Link } from 'react-router-dom';
import Swiper from 'react-id-swiper';
import {  pagination } from 'swiper';

import swipercore, { Autoplay } from 'swiper';
import 'swiper/css/autoplay';

import 'swiper/swiper-bundle.min.css';
import HeroBg1 from '../../assets/img/home1/hero1.jpg';
import HeroBg2 from '../../assets/img/home1/hero2.jpg';

const heroData = [
    {
        backgroundImage: HeroBg1,
        title: 'Dekoratif Beton',
        servicesLink: '/hizmetler',
        exploreLink: '/hakkimizda',
        exploreBtnText: 'İncele',
        exploreBtnStyle: { backgroundColor: '#0071DE', border: '2px solid #0071DE', fontWeight: 'medium', color: 'white' }
    },
    {
        backgroundImage: HeroBg2,
        title: 'Spor Zemin Sistemleri',
        servicesLink: '/hizmetler',
        exploreLink: '/hakkimizda',
        exploreBtnText: 'İncele',
        exploreBtnStyle: { backgroundColor: '#0071DE', border: '2px solid #0071DE', fontWeight: 'medium', color: 'white' }
    },
    {
        backgroundImage:'http://bmczemin.com.tr/wp-content/uploads/2023/08/Adsiz-tasarim.png',
        title:'Epoksi Zemin Kaplama',
        servicesLink: '/hizmetler',
        exploreLink: '/hakkimizda',
        exploreBtnText: 'İncele',
        exploreBtnStyle: { backgroundColor: '#0071DE', border: '2px solid #0071DE', fontWeight: 'medium', color: 'white' }
    },{
        backgroundImage:'http://bmczemin.com.tr/wp-content/uploads/2023/08/tas-hali-kaplama.png',
        title:'Taş Halı Kaplama',
        servicesLink: '/hizmetler',
        exploreLink: '/hakkimizda',
        exploreBtnText: 'İncele',
        exploreBtnStyle: { backgroundColor: '#0071DE', border: '2px solid #0071DE', fontWeight: 'medium', color: 'white' }
    },{
        backgroundImage:'http://bmczemin.com.tr/wp-content/uploads/2023/08/mikro-beton.png',
        title:'Mikro Beton',
        servicesLink: '/hizmetler',
        exploreLink: '/hakkimizda',
        exploreBtnText: 'İncele',
        exploreBtnStyle: { backgroundColor: '#0071DE', border: '2px solid #0071DE', fontWeight: 'medium', color: 'white' }
    }
];

function Hero1() {
    swipercore.use([Autoplay]);

    const params = {
        spaceBetween: 30,
        centeredSlides: true,
        autoplay: {
          delay: 250,
          disableOnInteraction: false
        },
        pagination: {
          el: '.swiper-pagination',
          clickable: true
        },
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev'
        }
      }

    return (
        <section className="hero-slide-wrapper hero-1">
                   <Swiper {...params}
  className="hero-slider-active owl-carousel">
                {heroData.map((slide, index) => (
                    <div key={index}>
                        <div className="single-slide bg-cover" style={{ backgroundImage: `url(${slide.backgroundImage})` }}>
                            <div className="container">
                                <div className="row">
                                    <div className="col-12 col-lg-8 col-xl-6">
                                        <div className="hero-contents">
                                            <h1>{slide.title}</h1>
                                            <Link to={slide.servicesLink} style={{ color: 'white', fontWeight: 'semibold' }} className="theme-btn font-weight-bold">
                                                Hizmetlerimiz <BsArrowRight style={{ marginLeft: '10px' }} />
                                            </Link>
                                            <Link to={slide.exploreLink} style={slide.exploreBtnStyle} className="theme-btn">
                                                {slide.exploreBtnText}
                                            </Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </Swiper>
        </section>
    );
}

export default Hero1;
